import { BaseComponent, SpinnerType } from "../../../base/base.component";
import * as i0 from "@angular/core";
import * as i1 from "ngx-spinner";
import * as i2 from "src/app/services/common/http-client.service";
export class ProductsComponent extends BaseComponent {
    constructor(spinnerService, httpClientService) {
        super(spinnerService);
        this.httpClientService = httpClientService;
    }
    async ngOnInit() {
        await this.showSpinner(SpinnerType.BallScaleMultiple);
        await this.httpClientService.post({
            controller: 'Products'
        }, {
            Name: "Angular Masa",
            Stock: 11,
            Price: 399.99
        }).subscribe((product) => {
            console.log('Eklenen ürün: ', product);
        });
        await this.httpClientService.get({
            controller: 'Products'
        }).subscribe((data) => {
            console.log(data);
        });
    }
}
