export class BaseComponent {
    constructor(spinner) {
        this.spinner = spinner;
    }
    showSpinner(spinnerNameType) {
        this.spinner.show(spinnerNameType).then(x => alert(x));
        // setTimeout(async () => {
        //   await this.hideSpinner(spinnerNameType);
        // }, 3000);
    }
    hideSpinner(spinnerNameType) {
        this.spinner.hide(spinnerNameType);
    }
}
export var SpinnerType;
(function (SpinnerType) {
    SpinnerType["BallAtom"] = "s1";
    SpinnerType["BallScaleMultiple"] = "s2";
    SpinnerType["BallSpinClockwiseFadeRotating"] = "s3";
})(SpinnerType || (SpinnerType = {}));
